import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import ImageListItem from "@material-ui/core/ImageListItem";
import ListSubheader from "@material-ui/core/ListSubheader";
// import ImageListItemBar from "@material-ui/core/ImageListItemBar";
// import Grid from "@material-ui/core/Grid";
// import IconButton from "@material-ui/core/IconButton";
// import InfoIcon from "@material-ui/icons/Info";
import Box from "@mui/material/Box";
import Masonry from "@mui/lab/Masonry";
import MasonryItem from "@mui/lab/MasonryItem";

// import BlankHeart from "@material-ui/icons/FavoriteBorder";
// import FilledHeart from "@material-ui/icons/Favorite";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "space-around",
    overflow: "hidden",
    backgroundColor: theme.palette.background.paper,
    widht: 1,
  },

  imageList: {
    width: 240,
    height: 420,
  },
  icon: {
    color: "rgba(255, 255, 255, 0.54)",
  },
}));

const TitleBarImageList = (props) => {
  const classes = useStyles();
  const { moviesList, imagePath } = props;
  const count = moviesList.length;

  const breakpoints = {
    default: 8,
    1100: 6,
    710: 4,
    480: 2,
  };

  return (
    <div>
      <ImageListItem
        className={classes.root}
        key="Subheader"
        style={{ height: "auto" }}
      >
        <ListSubheader component="div">{count} movies found.</ListSubheader>
      </ImageListItem>

      <Box sx={{ width: 1, minHeight: 829 }}>
        <Masonry columns={breakpoints} spacing={1}>
          {moviesList.map((item) => (
            <MasonryItem key={item.img}>
              <img
                src={imagePath + item.poster_path + `?w=162&auto=format`}
                srcSet={
                  imagePath + item.poster_path + `?w=162&auto=format&dpr=2 2x`
                }
                alt={item.title}
                loading="lazy"
              />
            </MasonryItem>
          ))}
        </Masonry>
      </Box>

      {/* <Grid container>
        {moviesList.map((item) => (
          <Grid spacing={{ xs: 2, md: 2 }} xs={6} sm={3} md={2}>
            <ImageListItem key={item.id} className={classes.imageList}>
              <img src={imagePath + item.poster_path} alt={item.title} />
              <ImageListItemBar
                title={item.title}
                subtitle={<span>Rating: {item.vote_average}</span>}
                actionIcon={
                  <IconButton
                    aria-label={`info about: ${item.overview}`}
                    className={classes.icon}
                  >
                    <InfoIcon />
                  </IconButton>
                }
              />
            </ImageListItem>
          </Grid>
        ))}
      </Grid> */}
    </div>
  );
};

export default TitleBarImageList;
