import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import ImageListItem from "@material-ui/core/ImageListItem";
import ListSubheader from "@material-ui/core/ListSubheader";

import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";

// import BlankHeart from "@material-ui/icons/FavoriteBorder";
// import FilledHeart from "@material-ui/icons/Favorite";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "space-around",
    overflow: "hidden",
    backgroundColor: theme.palette.background.paper,
    widht: 1,
  },
}));

const TitleBarImageList = (props) => {
  const classes = useStyles();
  const { moviesList, imagePath } = props;
  const count = moviesList.length;

  return (
    <div>
      <ImageListItem
        className={classes.root}
        key="Subheader"
        style={{ height: "auto" }}
      >
        <ListSubheader component="div">{count} movies found.</ListSubheader>
      </ImageListItem>

      <ResponsiveMasonry
        columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 3, 1100: 4, 1250: 5 }}
      >
        <Masonry>
          {moviesList.map((item) => (
            <Masonry
              sx={{ width: 240, minHeight: 320 }}
              key={item.id}
              onClick={() => {
                console.log(item.id + " " + item.title);
              }}
            >
              <img
                src={imagePath + item.poster_path + `?w=162&auto=format`}
                srcSet={
                  imagePath + item.poster_path + `?w=162&auto=format&dpr=1 1x`
                }
                alt={item.title}
                loading="lazy"
              />
            </Masonry>
          ))}
        </Masonry>
      </ResponsiveMasonry>

      {/* <Grid container>
        {moviesList.map((item) => (
          <Grid spacing={{ xs: 2, md: 2 }} xs={6} sm={3} md={2}>
            <ImageListItem key={item.id} className={classes.imageList}>
              <img src={imagePath + item.poster_path} alt={item.title} />
              <ImageListItemBar
                title={item.title}
                subtitle={<span>Rating: {item.vote_average}</span>}
                actionIcon={
                  <IconButton
                    aria-label={`info about: ${item.overview}`}
                    className={classes.icon}
                  >
                    <InfoIcon />
                  </IconButton>
                }
              />
            </ImageListItem>
          </Grid>
        ))}
      </Grid> */}
    </div>
  );
};

export default TitleBarImageList;
