  };

  const getMoviePath = (id) => {
    return "/movie/" + id;
  };

  return (
    <div>
      <p>{count} movies found.</p>
      <div className="flexbox-container">
        {moviesList.map((item) => (
          <div key={item.id} className="flexbox-item">
            <Link className="movieButton" to={getMoviePath(item.id)} {...props}>
              <img
                src={imagePath + item[imgTag] + `?w=162&auto=format`}
                srcSet={
                  imagePath + item[imgTag] + `?w=162&auto=format&dpr=2 2x`
                }
                alt={item[titleTag]}
                loading="lazy"
              ></img>
            </Link>
            {/* <Button
