import React, { Component } from "react";
import * as TagNames from "../constants/constants";
import axios from "axios";
// import { GetJSON } from "../common/getjson";

class Home extends Component {
  state = { movies: [] };

  async componentDidMount() {
    const { data: movies } = await axios.get(TagNames.getURL());
    // with fetchAPI - const data = GetJSON(url);
    // console.log("URL: " + url);

    this.setState({ movies });
  }

  render() {
    return (
      <>
        {this.state.movies.map((movie) => {
          return `<div><h3>${movie.title}</h3>
          <h4>${movie.vote_average}</h4></div>`;
        })}
      </>
    );
  }
}

export default Home;
