import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import ImageListItem from "@material-ui/core/ImageListItem";
import ListSubheader from "@material-ui/core/ListSubheader";

import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";

// import BlankHeart from "@material-ui/icons/FavoriteBorder";
// import FilledHeart from "@material-ui/icons/Favorite";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    overflow: "hidden",
    backgroundColor: theme.palette.background.paper,
  },
  image: {
    width: 240,
    height: 360,
    padding: 2,
  },
}));

const TitleBarImageList = (props) => {
  const classes = useStyles();
  const { moviesList, imagePath } = props;
  const count = moviesList.length;

  return (
    <div>
      <ImageListItem
        className={classes.root}
        key="Subheader"
        style={{ height: "auto" }}
      >
        <ListSubheader component="div">{count} movies found.</ListSubheader>
      </ImageListItem>

      <ResponsiveMasonry
        columnsCountBreakPoints={{
          350: 2,
          750: 3,
          910: 4,
          1050: 5,
          1300: 6,
          1650: 7,
          1850: 8,
        }}
      >
        <Masonry>
          {moviesList.map((item) => (
            <Masonry key={item.id}>
              <img
                onClick={() => {
                  console.log(item.overview);
                }}
                className={classes.image}
                src={imagePath + item.poster_path + `?w=162&auto=format`}
                srcSet={
                  imagePath + item.poster_path + `?w=162&auto=format&dpr=2 2x`
                }
                alt={item.title}
                loading="lazy"
              />
            </Masonry>
          ))}
        </Masonry>
      </ResponsiveMasonry>
    </div>
  );
};

export default TitleBarImageList;
