import React, { forwardRef } from "react";
import Form from "../common/form";
import Joi from "joi-browser";

// const create_username_pattern = "^[a-zA-Z0-9_]+$"; // username with lowerUpper alfanumeric and _ only.
// const create_password_pattern =
//   "^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[#$^+=!*()@%&])";

class LoginForm extends Form {
  state = {
    data: { username: "", password: "" },
    errors: {},
  };

  schema = {
    username: Joi.string()
      // .regex(RegExp(username_pattern))
      .alphanum()
      .required()
      .max(20)
      .label("Username"),
    password: Joi.string()
      // .regex(RegExp(password_pattern))
      .max(20)
      .label("Password"),
  };

  doSubmit = () => {
    // Call the server
    console.log("Submitted");
  };

  render() {
    return (
      <div className="container">
        <div className="Login">
          <h1>Log In</h1>
          <form onSubmit={this.handleSubmit}>
            {this.renderInput("username", "Username")}
            {this.renderInput("password", "Password", "password")}
            {this.renderButton("LogIn", forwardRef(this.onSubmit))}
          </form>
        </div>
      </div>
    );
  }
}

export default LoginForm;
