import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import ImageList from "@material-ui/core/ImageList";
import ImageListItem from "@material-ui/core/ImageListItem";
import ImageListItemBar from "@material-ui/core/ImageListItemBar";
import ListSubheader from "@material-ui/core/ListSubheader";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import InfoIcon from "@material-ui/icons/Info";

// import BlankHeart from "@material-ui/icons/FavoriteBorder";
// import FilledHeart from "@material-ui/icons/Favorite";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "space-around",
    overflow: "hidden",
    backgroundColor: theme.palette.background.paper,
    xs: 12,
    sm: 6,
    md: 3,
    widht: 1,
  },
  imageList: {
    width: 320,
    height: 1,
  },
  icon: {
    color: "rgba(255, 255, 255, 0.54)",
  },
}));

const TitleBarImageList = (props) => {
  const classes = useStyles();
  const { moviesList, imagePath } = props;
  const count = moviesList.length;

  return (
    <div className={classes.root}>
      <ImageList rowHeight={240} className={classes.imageList}>
        <ImageListItem key="Subheader" style={{ height: "auto" }}>
          <ListSubheader component="div">{count} movies found.</ListSubheader>
        </ImageListItem>
        {moviesList.map((item) => (
          <Grid xs={12} sm={6} md={3}>
            <ImageListItem key={item.id}>
              <img src={imagePath + item.poster_path} alt={item.title} />
              <ImageListItemBar
                title={item.title}
                subtitle={<span>Rating: {item.vote_average}</span>}
                actionIcon={
                  <IconButton
                    aria-label={`info about: ${item.overview}`}
                    className={classes.icon}
                  >
                    <InfoIcon />
                  </IconButton>
                }
              />
            </ImageListItem>
          </Grid>
        ))}
      </ImageList>
    </div>
  );
};

export default TitleBarImageList;
