import React, { Components } from "react";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import Input from "../common/input";

const schema = yup.object().shape({
  username: yup.string().required("Username required.").min(5),
  password: yup.string().required("Password required.").min(5),
});

class Login extends Components {
  state = ({ register, handleSubmit, errors } = useForm({
    defaultValues: { username: "", password: "" },
    mode: "onBlur",
    resolver: yupResolver(schema),
  }));

  onSubmit = (e) => {
    e.preventDefault();
    console.log("onsumbit clicked");
  };

  render() {
    const { register, handleSubmit, errors } = this.state;
    return (
      <div className="Login">
        <div>
          <form onSubmit={handleSubmit(this.onSubmit)}>
            <input
              ref={register}
              name="username"
              label="Username"
              type="text"
            />
            <input
              ref={register}
              name="password"
              label="Password"
              type="password"
            />
            <button>Submit</button>
          </form>
        </div>
      </div>
    );
  }
}

export default Login;
