import React, { useContext } from "react";
import MovieContext from "../js/contexts/movieContext";
import * as TagNames from "../js/constants/constants";
import PropTypes from "prop-types";
const MovieDetails = (props) => {
  const { movies } = useContext(MovieContext);
  const id = props.match.params.id;
  const selectedMovie = movies.filter(async (m) => {
    return m.id === parseInt(id);
  });
  const movie = selectedMovie[0];
  console.log(movie.title);

  return (
    <div className="container">
      <h1>Movie {movie.title}</h1>
      <img
        className="image"
        src={TagNames.getImagePath(movie[img_tag])}
        srcSet={TagNames.getImagePath(movie[img_tag])}
        alt={movie[title_tag]}
        loading="lazy"
      ></img>
    </div>
  );
};

MovieDetails.defaultProps = {
  img_tag: "poster_path",
  title_tag: "title",
};

export default MovieDetails;
