import React, { Component } from "react";
import * as TagNames from "../constants/constants";
import VideoPlayer from "../common/videoPlayer";
import MoviesList from "../common/moviesList";
import axios from "axios";
import "@material-ui/core";

class Home extends Component {
  state = {
    results: [],
    videoLink: "",
    video: false,
    transform: 0,
  };

  componentDidMount = async () => {
    window.addEventListener("scroll", this.handleScroll);
    const { data } = await axios.get(TagNames.getURL());
    const { results } = data;
    this.setState({ results });
  };

  componentWillUnmount = () => {
    window.removeEventListener("scroll", this.handleScroll);
  };

  handleScroll = (event) => {
    let scrollTop = event.srcElement.body.scrollTop,
      itemTranslate = Math.min(0, scrollTop / 3 - 60);
    this.setState({ transform: itemTranslate });
  };

  handleVideo = async (id) => {
    this.handleScroll("home");
    const { data } = await axios.get(TagNames.getVideoKey(id));
    const vkey = (await data.results[0]?.key) ? data.results[0].key : "N/A";
    this.getVideoKey(vkey);
  };

  handleVideoClose = (status) => {
    this.setState({ video: status });
  };

  getVideoKey(vkey) {
    if (vkey !== "N/A") {
      const videoLink = TagNames.getVideoLink(vkey);
      this.setState({ video: true });
      this.setState({ videoLink });
      console.log(videoLink);
    } else {
      this.setState({ video: false });
      console.log("Video not available.");
    }
  }

  render() {
    const { results, videoLink, video } = this.state;
    const imagepath = TagNames.getImagePath();
    if (results.length === 0) {
      return <h3>Movies not found.</h3>;
    }

    return (
      <div className="home">
        <MoviesList
          moviesList={results}
          imagePath={imagepath}
          getVideo={this.handleVideo}
        />

        <VideoPlayer
          trailerLink={videoLink}
          status={video}
          handleVideoClose={this.handleVideoClose}
        />
      </div>
    );
  }
}

export default Home;
