import { Fragment, Component } from "react";
import { Link } from "react-router-dom";
import * as TagName from "../../constants/constants";
import logo from "../../images/logo.png";

class NavBar extends Component {
  state = { clicked: false };

  //----------------Render -----------
  render() {
    return (
      <Fragment>
        <header className="Navbar">
          <a className="brandLogo" href="/#">
            <img src={logo} alt={TagName.COMPANY_NAME} />
          </a>

          <ul className={TagName.MENU_DEFAULT}>
            <li>
              {TagName.menus.map((m) => {
                return (
                  <Link to={m.name} key={m.name}>
                    {m.name}
                  </Link>
                );
              })}
            </li>
          </ul>

          <div
            onClick={this.handleClicked}
            className={
              this.state.clicked
                ? TagName.HAMBURGER_OPEN
                : TagName.HAMBURGER_CLOSE
            }
          >
            <div className="hamburger_lines"></div>
          </div>
        </header>

        <ul
          className={
            this.state.clicked ? TagName.MOBILE_OPEN : TagName.MOBILE_CLOSE
          }
        >
          <li>
            {TagName.menus.map((m) => {
              return (
                <Link to={m.name} key={m.name} onClick={this.handleClicked}>
                  {m.name}
                </Link>
              );
            })}
          </li>
        </ul>
      </Fragment>
    );
  }

  handleClicked = () => {
    this.setState({ clicked: !this.state.clicked });
  };
}

export default NavBar;
