import { IconButton } from "@material-ui/core";
import CloseIcon from "@mui/icons-material/Close";
import React from "react";
import "../scss/style.css";
const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <div>
        <iframe
          title="user"
          width="320"
          height="240"
          src={trailerLink}
        ></iframe>

        <CloseIcon onClick={() => handleVideoClose(false)} color="primary">
          add_circle
        </CloseIcon>
      </div>
    </div>
  );
};

export default VideoPlayer;
