import React, { Components } from "react";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import Input from "../common/input";

const schema = yup.object().shape({
  username: yup.string().required("Username required.").min(5),
  password: yup.string().required("Password required.").min(5),
});

class Login extends Components {
  state = ({ register, handleSubmit, errors } = useForm({
    defaultValues: { username: "", password: "" },
    mode: "onBlur",
    resolver: yupResolver(schema),
  }));

  onSubmit = (e) => {
    e.preventDefault();
    console.log("onsumbit clicked");
  };

  render() {
    return (
      <div className="Login">
        <div>
          <form onSubmit={this.state.handleSubmit(this.onSubmit)}>
            <Input
              ref={register}
              name="username"
              label="Username"
              type="text"
              error={!!errors}
              helperText={errors?.username?.message}
            />

            <Input
              ref={register}
              name="password"
              label="Password"
              type="password"
              error={!!errors}
              helperText={errors?.password?.message}
            />
            <button>Submit</button>
          </form>
        </div>
      </div>
    );
  }
}

export default Login;
