import React, { Component } from "react";
import axios from "axios";
import * as TagNames from "../constants/constants";
import VideoPlayer from "../common/videoPlayer";
import MoviesList from "../common/moviesList";
import PaginationBar from "../common/paginationBar";
import { Paginate } from "../common/js/paginate";

class Home extends Component {
  // constructor() {
  //   super();
  //   this.handleScroll = this.handleScroll.bind(this);
  // }

  state = {
    results: [],
    videoLink: "",
    video: false,
    transform: 0,
    currentPage: 1,
    pageSize: 4,
  };

  componentDidMount = async () => {
    window.addEventListener("scroll", this.handleScroll);
    const { data } = await axios.get(TagNames.getURL());
    const { results } = data;
    this.setState({ results });
  };

  componentWillUnmount = () => {
    window.removeEventListener("scroll", this.handleScroll);
  };

  handleScroll = (event) => {
    let scrollTop = event.target.scrollingElement.scrollTop,
      itemTranslate = Math.min(0, scrollTop / 3 - 60);
    this.setState({ transform: itemTranslate });
  };

  handleVideo = async (id) => {
    const { data } = await axios.get(TagNames.getVideoKey(id));
    const videoLink = (await data.results[0]?.key)
      ? TagNames.getVideoLink(data.results[0].key)
      : "";

    const video = videoLink ? true : false;
    this.setState({ videoLink, video });
  };

  handleVideoClose = (status) => {
    this.setState({ video: status });
  };

  handlePageChange = (currentPage) => {
    console.log(currentPage);
    this.setState({ currentPage });
  };

  render() {
    const { results, videoLink, video, currentPage, pageSize } = this.state;
    const imagepath = TagNames.getImagePath();
    const movies = Paginate(results, currentPage, pageSize);
    const count = results.length;

    if (results.length === 0) {
      return <h3>Movies not found.</h3>;
    }

    return (
      <div className="container">
        <div className="home">
          <MoviesList
            moviesList={movies}
            count={count}
            imagePath={imagepath}
            getVideo={this.handleVideo}
          />

          <PaginationBar
            itemCount={count}
            pageSize={pageSize}
            currentPage={1}
            onPageChange={this.handlePageChange}
          />

          <VideoPlayer
            trailerLink={videoLink}
            status={video}
            handleVideoClose={this.handleVideoClose}
          />
        </div>
      </div>
    );
  }
}

export default Home;
