import React from "react";
import { Close } from "@material-ui/icons";
import { Button } from "@material-ui/core";

const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <iframe title="user" width="320" height="240" src={trailerLink}></iframe>
      <Button
        style={{
          maxWidth: "36px",
          minWidth: "36px",
          backgroundColor: "#cf0f0f",
          color: "#ffffff",
          borderRadius: "20px",
        }}
        variant="outlined"
        align="center"
        onClick={() => handleVideoClose(false)}
      >
        <Close />
      </Button>
    </div>
  );
};

export default VideoPlayer;
