import React, { Component } from "react";
import { Link, NavLink, Route } from "react-router-dom";
import * as TagName from "../../constants/constants";
import logo from "../../images/gogixer.png";
import UserContext from "../../Contexts/userContext";
import { useState, useContext } from "react";

const NavBar = (props) => {
  const [clicked, setClicked] = useState(false);
  const { currentUser, onLogin, onLogout } = useContext(UserContext);

  const handleClicked = () => {
    setClicked(!clicked);
  };

  const handleLogout = () => {
    onLogout();
    props.history.replace("/");
  };

  return (
    <div className="Navigation">
      <div>
        <header className="Navbar">
          <a className="brandLogo" href="/#">
            <img src={logo} alt={TagName.COMPANY_NAME} />
          </a>

          <ul className={TagName.MENU_DEFAULT}>
            <li>
              {TagName.menus.map((m) => {
                return (
                  <NavLink to={m.path} key={m.name}>
                    {m.name}
                  </NavLink>
                );
              })}

              <NavLink
                to="/login"
                key="signin"
                id={currentUser.length === 0 ? "signin" : "hidden"}
              >
                Sign In
              </NavLink>
              <NavLink
                to="/signup"
                key="signup"
                id={currentUser.length === 0 ? "signup" : "hidden"}
              >
                Sign Up
              </NavLink>

              <div
                key="logout"
                id={currentUser.length === 0 ? "hidden" : "logout"}
                onClick={this.handleLogout}
              >
                Logout
              </div>
            </li>
          </ul>
          <div
            onClick={this.handleClicked}
            className={
              this.state.clicked
                ? TagName.HAMBURGER_OPEN
                : TagName.HAMBURGER_CLOSE
            }
          >
            <div className="hamburger_lines"></div>
          </div>
        </header>
      </div>
      <ul
        className={
          this.state.clicked ? TagName.MOBILE_OPEN : TagName.MOBILE_CLOSE
        }
      >
        <li>
          {TagName.menus.map((m) => {
            return (
              <Link to={m.path} key={m.name} onClick={this.handleClicked}>
                {m.name}
              </Link>
            );
          })}

          <Link
            to="/login"
            key="login"
            id="signin"
            onClick={this.handleClicked}
          >
            Sign In
          </Link>
          <Link
            to="/signup"
            key="signup"
            id="signup"
            onClick={this.handleClicked}
          >
            Sign Up
          </Link>
        </li>
      </ul>
    </div>
  );
};

export default NavBar;
