import React from "react";
import { PlayArrow } from "@material-ui/icons";
import { Button } from "@material-ui/core";
import { useWindowScroll } from "react-use";

const MoviesList = (props) => {
  const { y: pageYOffset } = useWindowScroll();
  const { moviesList, imagePath, getVideo } = props;
  const count = moviesList.length;
  const scrollToTop = () => {
    window.scrollTo({ Top: 0, behavior: "smooth" });
  };

  return (
    <div>
      <p>{count} movies found.</p>
      <div className="flexbox-container">
        {moviesList.map((item) => (
          <div key={item.id} className="flexbox-item">
            <img
              src={imagePath + item.poster_path + `?w=162&auto=format`}
              srcSet={
                imagePath + item.poster_path + `?w=162&auto=format&dpr=2 2x`
              }
              alt={item.title}
              loading="lazy"
            />

            <Button
              style={{ maxWidth: "36px", minWidth: "36px", borderRadius: 20 }}
              variant="outlined"
              align="center"
              onClick={() => getVideo(item.id)}
            >
              <PlayArrow style={{ color: "#ffffff" }} />
            </Button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MoviesList;
