import React from "react";
import "../scss/style.css";
const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <div>
        <iframe title="user" width="320" height="240" src={trailerLink}>
          <button
            className="CloseButton"
            onClick={() => handleVideoClose(false)}
          >
            Close
          </button>
        </iframe>
      </div>
    </div>
  );
};

export default VideoPlayer;
