import { Fragment, Component } from "react";
import { Link } from "react-router-dom";
import * as TagName from "../constants/constants";
import brandLogo from "../images/logo.png";
// import "bootstrap/dist/css/bootstrap.css";
// import "bootstrap/dist/js/bootstrap";

class NavBar extends Component {
  state = { clicked: false };

  //----------------Render -----------
  render() {
    return (
      <Fragment>
        {/* <nav class="navbar navbar-light bg-light fixed-top">
          <div class="container-fluid">
            <a class="navbar-brand" href="#">
              Offcanvas navbar
            </a>
            <button
              class="navbar-toggler"
              type="button"
              data-bs-toggle="offcanvas"
              data-bs-target="#offcanvasNavbar"
              aria-controls="offcanvasNavbar"
            >
              <span class="navbar-toggler-icon"></span>
            </button>
            <div
              class="offcanvas offcanvas-end"
              tabindex="-1"
              id="offcanvasNavbar"
              aria-labelledby="offcanvasNavbarLabel"
            >
              <div class="offcanvas-header">
                <h5 class="offcanvas-title" id="offcanvasNavbarLabel">
                  Offcanvas
                </h5>
                <button
                  type="button"
                  class="btn-close text-reset"
                  data-bs-dismiss="offcanvas"
                  aria-label="Close"
                ></button>
              </div>
            </div>
          </div>
        </nav> */}

        <header className="Navbar">
          <a className="brandLogo" href="/#">
            <img src={brandLogo} alt={TagName.COMPANY_NAME} />
          </a>

          <ul
            className={
              !this.state.clicked ? TagName.MENU_DEFAULT : TagName.MENU_MOBILE
            }
          >
            <li>
              <Link to="/">Home</Link>
              <Link to="/shop">Shop</Link>
              <Link to="/services">Services</Link>
              <Link to="/blog">Blog</Link>
              <Link to="/about">About Us</Link>
              <Link to="/contact">Contact</Link>
            </li>
          </ul>

          <div
            onClick={this.handleClicked}
            className={
              this.state.clicked
                ? TagName.HAMBURGER_OPEN
                : TagName.HAMBURGER_CLOSE
            }
          >
            <div className="hamburger_lines"></div>
          </div>
        </header>
      </Fragment>
    );
  }

  handleClicked = () => {
    this.setState({ clicked: !this.state.clicked });
  };

  handleMenuClicked = () => {
    if (this.state.clicked) this.setState({ clicked: !this.state.clicked });
  };
}

export default NavBar;
