import React from "react";
import Button from "@mui/material/Button";
import PlayArrow from "@mui/icons-material/PlayArrow";
import { Link } from "react-router-dom";
const MoviesList = (props) => {
  const { moviesList, count, imagePath, getVideo, imgTag, titleTag } = props;

  return (
    <div>
      <p>{count} movies found.</p>
      <div className="flexbox-container">
        {moviesList.map((item) => (
          <div key={item.id} className="flexbox-item">
            <img
              src={imagePath + item[imgTag] + `?w=162&auto=format`}
              srcSet={imagePath + item[imgTag] + `?w=162&auto=format&dpr=2 2x`}
              alt={item[titleTag]}
              loading="lazy"
            ></img>

            {/* <Button
              style={{ maxWidth: "36px", minWidth: "36px", borderRadius: 20 }}
              variant="outlined"
              align="center"
              onClick={() => getVideo(item.id)}
            >
              <PlayArrow style={{ color: "#ffffff" }} />
            </Button> */}
            <div className="movieButton">
              <Link to={item.id}>{item[titleTag]}</Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

MoviesList.defaultProps = {
  imgTag: "poster_path",
  titleTag: "title",
};

export default MoviesList;
