import { Fragment, Component } from "react";
import { Link } from "react-router-dom";
import * as TagName from "../constants/constants";
import brandLogo from "../images/logo.png";
// import "bootstrap/dist/css/bootstrap.css";
// import "bootstrap/dist/js/bootstrap";

class NavBar extends Component {
  state = { clicked: false };

  //----------------Render -----------
  render() {
    return (
      <Fragment>
        <header className="Navbar">
          <a className="brandLogo" href="/#">
            <img src={brandLogo} alt={TagName.COMPANY_NAME} />
          </a>

          <ul className={TagName.MENU_DEFAULT}>
            <li>
              <Link to="/">Home</Link>
              <Link to="/shop">Shop</Link>
              <Link to="/services">Services</Link>
              <Link to="/blog">Blog</Link>
              <Link to="/about">About Us</Link>
              <Link to="/contact">Contact</Link>
              <Link to="/login">Login</Link>
            </li>
          </ul>

          <div
            onClick={this.handleClicked}
            className={
              this.state.clicked
                ? TagName.HAMBURGER_OPEN
                : TagName.HAMBURGER_CLOSE
            }
          >
            <div className="hamburger_lines"></div>
          </div>
        </header>

        <ul
          className={
            this.state.clicked ? TagName.MOBILE_OPEN : TagName.MOBILE_CLOSE
          }
        >
          <li>
            <Link to="/" onClick={this.handleClicked}>
              Home
            </Link>
            <Link to="/shop" onClick={this.handleClicked}>
              Shop
            </Link>
            <Link to="/services" onClick={this.handleClicked}>
              Services
            </Link>
            <Link to="/blog" onClick={this.handleClicked}>
              Blog
            </Link>
            <Link to="/about" onClick={this.handleClicked}>
              About Us
            </Link>
            <Link to="/contact" onClick={this.handleClicked}>
              Contact
            </Link>
            <Link to="/login" onClick={this.handleClicked}>
              Login
            </Link>
          </li>
        </ul>
      </Fragment>
    );
  }

  handleClicked = () => {
    this.setState({ clicked: !this.state.clicked });
  };

  handleMenuClicked = () => {
    if (this.state.clicked) this.setState({ clicked: !this.state.clicked });
  };
}

export default NavBar;
