import React from "react";
import { Button } from "bootstrap";
import "../scss/style.css";
const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <Button className="CloseButton" onClick={() => handleVideoClose(false)}>
        Close
      </Button>
      <iframe title="user" width="320" height="240" src={trailerLink}></iframe>
    </div>
  );
};

export default VideoPlayer;
