import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import ImageListItem from "@material-ui/core/ImageListItem";
import ListSubheader from "@material-ui/core/ListSubheader";
import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";
import { PlayArrow } from "@material-ui/icons";

const imageWidth = 180,
  imageheight = 280;

const TitleBarImageList = (props) => {
  const classes = useStyles();
  const { moviesList, imagePath, getVideo } = props;
  const count = moviesList.length;

  return (
    <div>
      <h6>{count} movies found.</h6>

      <ResponsiveMasonry
        columnsCountBreakPoints={{
          360: 2,
          540: 3,
          720: 4,
          900: 5,
          1080: 6,
          1260: 7,
          1440: 8,
          1620: 9,
        }}
      >
        <Masonry>
          {moviesList.map((item) => (
            <Masonry key={item.id}>
              <img
                className={classes.image}
                src={imagePath + item.poster_path + `?w=162&auto=format`}
                srcSet={
                  imagePath + item.poster_path + `?w=162&auto=format&dpr=2 2x`
                }
                alt={item.title}
                loading="lazy"
              />
              {/* <button className={PlayButton}>

              </button> */}
              <button className="PlayButton" onClick={() => getVideo(item.id)}>
                <PlayArrow />
              </button>
            </Masonry>
          ))}
        </Masonry>
      </ResponsiveMasonry>
    </div>
  );
};

export default TitleBarImageList;
