import React, { Component } from "react";
// import { useForm } from "react-hook-form";
// import * as Yup from "yup";
import Input from "../common/input";
import * as yup from "yup";

class Login extends Component {
  state = {
    username: "",
    password: "",
  };

  schema = yup.object().shape({
    username: yup.string().required("Username required.").min(5),
    password: yup.string().required("Password required.").min(5),
  });

  onValidate = () => {
    const result = yup.onValidate(this.state, this.schema);
  };

  onSubmit = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div className="Login">
        <div>
          <form onSubmit={this.onSubmit}>
            <Input name="username" label="Username" type="text" required />
            <button>Submit</button>
          </form>
        </div>
      </div>
    );
  }
}

export default Login;
