import React, { Component } from "react";
import * as TagNames from "../constants/constants";
import axios from "axios";
// import Movies from "../common/moviesList";
// import { GetJSON } from "../common/getjson";
import TitleBarImageList from "../common/imagelist";
import VideoPlayer from "../common/videoPlayer";

class Home extends Component {
  state = {
    results: [],
    videoLink: "",
  };

  async componentDidMount() {
    const { data } = await axios.get(TagNames.getURL());
    const { results } = data;
    console.log(
      "ðŸš€ ~ file: home.jsx ~ line 16 ~ Home ~ componentDidMount ~ results",
      results
    );
    this.setState({ results });
  }

  handleVideo = async (id) => {
    const { data } = await axios.get(TagNames.getVideoKey(id));
    videoLink = TagNames.getVideoLink(data.results[0].key);
    console.log(videoLink);
  };

  render() {
    const { results, videoLink } = this.state;
    const imagepath = TagNames.getImagePath();
    if (results.length === 0) {
      return <h3>Movies not found.</h3>;
    }
    if (videoLink.length > 0) {
      return (
        <iframe
          width="420"
          height="315"
          src={this.videoLink + "?autoplay=1"}
        ></iframe>
      );
    }
    return (
      <div>
        <TitleBarImageList
          moviesList={results}
          imagePath={imagepath}
          getVideo={this.handleVideo}
        />
      </div>
    );
    // <Movies moviesList={results} imagePath={imagepath} />;
  }
}

export default Home;
