import React from "react";
import { Close } from "@material-ui/icons";
import "../scss/style.css";
import { Button } from "@material-ui/core";

const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <div className="videobox">
        <iframe
          title="user"
          width="320"
          height="240"
          src={trailerLink}
        ></iframe>
        <br />
        <Button
          style={{
            maxWidth: "36px",
            minWidth: "36px",
            backgroundColor: "#cf0f0f",
            color: "#ffffff",
            borderRadius: "20px",
          }}
          variant="outlined"
          align="center"
          onClick={() => handleVideoClose(false)}
        >
          <Close />
        </Button>
      </div>
    </div>
  );
};

export default VideoPlayer;
