import React from "react";
import Button from "@mui/material/Button";
import Close from "@mui/icons-material/Cancel";

const VideoPlayer = ({ trailerLink, status, handleVideoClose }) => {
  return (
    <div>
      <span>{!status ? "Trailer-Not Available" : "Trailer-Available"}</span>
      <div className={status ? "YTO" : "YTC"}>
        <iframe title="video" src={trailerLink}></iframe>
        <Button
          style={{
            maxWidth: "36px",
            minWidth: "36px",
            backgroundColor: "#cf0f0f",
            color: "#ffffff",
            borderRadius: "20px",
          }}
          variant="outlined"
          align="center"
          onClick={() => handleVideoClose(false)}
        >
          <Close />
        </Button>
      </div>
    </div>
  );
};

export default VideoPlayer;
