import { Button } from "@material-ui/core";
import React from "react";

const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <Button onClick={() => handleVideoClose(false)}>Close</Button>
      <iframe
        width="420"
        height="315"
        src={trailerLink + "?autoplay=1"}
      ></iframe>
    </div>
  );
};

export default VideoPlayer;
