import React from "react";
import Button from "@mui/material/Button";
import Close from "@mui/icons-material/Cancel";

const VideoPlayer = (props) => {
  const { trailerLink, status, handleVideoClose } = props;
  return (
    <div className={status === true ? "YTO" : "YTC"}>
      <div>
        <iframe title="video" src={trailerLink}></iframe>
        <Button
          style={{
            maxWidth: "36px",
            minWidth: "36px",
            backgroundColor: "#cf0f0f",
            color: "#ffffff",
            borderRadius: "20px",
          }}
          variant="outlined"
          align="center"
          onClick={() => handleVideoClose(false)}
        >
          <Close style={{ backgroundColor: "#cf0f0f" }} />
        </Button>
      </div>
    </div>
  );
};

export default VideoPlayer;
