import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import * as TagNames from "../constants/constants";
import VideoPlayer from "../common/videoPlayer";
import MoviesList from "../common/moviesList";
import PaginationBar from "../common/paginationBar";
import { Paginate } from "../common/js/paginate";
import UserContext from "../Contexts/userContext";
import MovieContext from "../Contexts/movieContext";

const Home = () => {
  const { currentUser } = useContext(UserContext);
  const { moviesList, addMovies } = useContext(MovieContext);

  const [trending, setTrending] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(20);
  const [videoLink, setVideoLink] = useState("");
  const [videoStatus, setVideoStatus] = useState(false);
  const imagepath = TagNames.getImagePath();
  const movies = Paginate(trending, currentPage, pageSize);
  const count = trending.length;

  useEffect(() => {
    const abortController = new AbortController();
    if (trending.length === 0) {
      async function fetchData(URL, { signal }) {
        // You can await here
        const { data } = await axios.get(URL);
        setTrending(data.results);
      }
      fetchData(TagNames.getURL(), abortController.signal);
      return function cleanup() {
        abortController.abort();
      };
    }
    console.log(trending);
  }, [trending]); // Or [] if effect doesn't need props or state

  const handleVideo = async (id) => {
    const { data } = await axios.get(TagNames.getVideoKey(id));
    const videoLink = (await data.results[0]?.key)
      ? TagNames.getVideoLink(data.results[0].key)
      : "";

    const video = videoLink ? true : false;
    setVideoLink(videoLink);
    setVideoStatus(video);
  };

  const handleVideoClose = (status) => {
    setVideoStatus(status);
  };

  const handlePageChange = (currentPage) => {
    console.log(currentPage);
    setCurrentPage(currentPage);
  };

  if (movies.length === 0) {
    return <h3>Movies not found.</h3>;
  }

  return (
    <div className="container">
      <div>Username: {currentUser.name}</div>
      <div className="home">
        <MoviesList
          movies={movies}
          count={count}
          image_path={imagepath}
          get_video={handleVideo}
        />

        {/* <PaginationBar
          itemCount={count}
          pageSize={pageSize}
          currentPage={1}
          onPageChange={handlePageChange}
        />

        <VideoPlayer
          trailerLink={videoLink}
          status={videoStatus}
          handleVideoClose={handleVideoClose}
        /> */}
      </div>
    </div>
  );
};

export default Home;

