import React, { Component } from "react";
import { Link } from "react-router-dom";
import * as TagName from "../../constants/constants";
import logo from "../../images/gogixer.png";

class NavBar extends Component {
  state = { clicked: false };

  //----------------Render -----------
  render() {
    return (
      <div className="Navigation">
        <div>
          <header className="Navbar">
            <a className="brandLogo" href="/#">
              <img src={logo} alt={TagName.COMPANY_NAME} />
            </a>

            <ul className={TagName.MENU_DEFAULT}>
              <li>
                {TagName.menus.map((m) => {
                  return (
                    <Link to={m.path} key={m.name}>
                      {m.name}
                    </Link>
                  );
                })}
                <Link to="" key="signup" id="signup">
                  Sign Up
                </Link>
              </li>
            </ul>

            <div
              onClick={this.handleClicked}
              className={
                this.state.clicked
                  ? TagName.HAMBURGER_OPEN
                  : TagName.HAMBURGER_CLOSE
              }
            >
              <div className="hamburger_lines"></div>
            </div>
          </header>
        </div>
        <ul
          className={
            this.state.clicked ? TagName.MOBILE_OPEN : TagName.MOBILE_CLOSE
          }
        >
          <li>
            {TagName.menus.map((m) => {
              return (
                <Link to={m.path} key={m.name} onClick={this.handleClicked}>
                  {m.name}
                </Link>
              );
            })}
            <Link to="/signup" key="signup" id="signup">
              Sign Up
            </Link>
          </li>
        </ul>
      </div>
    );
  }

  handleClicked = () => {
    this.setState({ clicked: !this.state.clicked });
  };
}

export default NavBar;
