import React, { useState, useContext, useEffect } from "react";
import MovieContext from "../js/contexts/movieContext";
import * as TagNames from "../js/constants/constants";

const MovieDetails = (props) => {
  const { movies } = useContext(MovieContext);
  const [movie, setMovie] = useState([]);
  const id = props.match.params.id;

  useEffect(() => {
    const abortController = new AbortController();
    if (movie.length === 0) {
      async function fetchData(id, movies, { signal }) {
        // You can await here
        const selectedMovie = movies.filter((m) => {
          return m.id === parseInt(id);
        });
        setMovie(selectedMovie[0]);
      }
      fetchData(id, abortController.signal);
      return function cleanup() {
        abortController.abort();
      };
    } else {
      console.log("data already fetched");
    }
  }, [id]);

  console.log(movie);

